import React, {useState} from 'react';
import axios from 'axios';

const EvaluateRule = () => {

    const [ ruleName, setRuleName ] = useState('');
    const [ dataJSON, setDataJSON ] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();

        axios.post('',{
            rule_string: ruleName,
            attributes: JSON.parse(dataJSON)
        }).then(response => {
            alert('Evaluation Result:' + response.data.result);
        }).catch(error => {
            alert('Error Evaluting Rule', error);
        })
    };
 
  return (
    <>
        <div>
            <h2>Evaluate Rule</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Rule Name:
                    <input type='text' value={ruleName} onChange={(e) => setRuleName} />
                </label>
            </form>
        </div>
    </>
  )
}

export default EvaluateRule
