import React, { useState } from 'react';
import axios from 'axios';

function EvaluateRule() {
  const [ruleString, setRuleString] = useState('');
  const [evaluationResult, setEvaluationResult] = useState(null);

  const handleEvaluate = (e) => {
    e.preventDefault();
    axios.post('http://127.0.0.1:5000/evaluate_rule', {
      rule: ruleString,
    })
    .then(response => {
      setEvaluationResult(response.data.evaluation_result);
      setRuleString('');
    })
    .catch(error => {
      console.error('Error Evaluating Rule:', error);
      alert('Error Evaluating Rule: ' + error.message);
    });
  };

  return (
    <div className="bg-gray-200 p-6 rounded-lg shadow-lg w-full max-w-lg mx-auto">
      <h2 className="text-2xl font-semibold text-gray-800 mb-4">Evaluate Rule</h2>
      <form onSubmit={handleEvaluate} className="space-y-4">
        <label className="block text-gray-700">
          Rule:
          <input
            type="text"
            value={ruleString}
            onChange={(e) => setRuleString(e.target.value)}
            required
            className="mt-1 w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400 transition duration-300"
          />
        </label>
        <button type="submit" className="w-full bg-blue-500 text-white font-semibold py-2 rounded-lg hover:bg-blue-600 transition duration-300">Evaluate Rule</button>
      </form>
      {evaluationResult !== null && (
        <p className="mt-4 p-4 bg-green-100 border border-green-400 font-semibold text-green-800 rounded-md">Evaluation Result: {evaluationResult.toString()}</p>
      )}
    </div>
  );
}

export default EvaluateRule;
