import React, { useState } from 'react';
import axios from 'axios';

const CombineRules = () => {
    const [selectedRules, setSelectedRules] = useState([]);
    const [operators, setOperators] = useState([]);
    const [combinedRule, setCombinedRule] = useState("");
    const [error, setError] = useState("");

    const handleRuleChange = (event) => {
        const { value } = event.target;
        setSelectedRules(prev => [...prev, value]);
    };

    const handleOperatorChange = (event) => {
        const { value } = event.target;
        setOperators(prev => [...prev, value]);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post("http://localhost:5000/combine_rules", {
                rules: selectedRules,
                operators: operators
            });

            // Check if the response data exists
            if (response.data) {
                setCombinedRule(response.data.data); // Assuming the response contains the combined rule
                setError(""); // Clear any previous error
            } else {
                console.error("Unexpected response format:", response);
                setError("Unexpected response format");
            }
        } catch (error) {
            console.error("Error combining rules:", error);
            setError("Failed to combine rules. Please try again.");
        }
    };

    return (
        <div>
            <h2>Combine Rules</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Rules:</label>
                    <select onChange={handleRuleChange}>
                        <option value="">Select a rule</option>
                        <option value="Rule A">Rule A</option>
                        <option value="Rule B">Rule B</option>
                        <option value="Rule C">Rule C</option>
                    </select>
                </div>
                <div>
                    <label>Operators:</label>
                    <select onChange={handleOperatorChange}>
                        <option value="">Select an operator</option>
                        <option value="AND">AND</option>
                        <option value="OR">OR</option>
                    </select>
                </div>
                <button type="submit">Combine Rules</button>
            </form>
            {combinedRule && <h3>Combined Rule: {combinedRule}</h3>}
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
    );
};

export default CombineRules;
