import React, {useState} from 'react';
import axios from 'axios';

const CombineRules = () => {

    const [rules, setRules] = useState([{ ruleName: '', operator: 'AND' }]);
    
    function handleAddRule(){
        setRules([ ...rules, { ruleName: '', operator: 'AND' }]);
    }

    const handleRuleChange = (index, field, value) => {
        const newRules = [...rules]
        newRules[index][field] = value;
        setRules(newRules);
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        const ruleNames = rules.map(rule => rule.ruleName);
        const operators = rules.map(rule => rule.operator);

        axios.post(' http://127.0.0.1:5000/combine_rules', {
            rules: ruleNames,
            operators: operators
        }).then(response => {
            alert('Combined Rule: ' + response.data.combined_rule);
        }).catch(error => {
            alert('Error Comining Rules', error);
        })
    }

  return (
    <>
        <h2>Combine Rules</h2>
        <form onSubmit={() => handleSubmit}>
            {
                rules.map((rule, index) => (
                    <div key={index}>
                        <label>
                            Rule { index + 1 }:
                            <input
                                type='text' 
                                value={rule.ruleName}
                                onChange={(e) => handleRuleChange(index, 'ruleName', e.target.value)}
                                required
                            />
                            {
                                index < rules.length && (
                                    <div>
                                        <label>
                                            Operator:
                                            <select
                                                value={rule.operator}
                                                onChange={(e) => handleRuleChange(index, 'operator', e.target.value)}
                                            >
                                                <option value="AND">AND</option>
                                                <option value="OR">OR</option>
                                            </select>
                                        </label>
                                    </div>
                                )
                            }
                        </label>
                    </div>
                ))}
                <button type="button" onClick={handleAddRule}>Add Another Rule</button>
                <button type="submit">Combine Rules</button>
        </form>
    </>
  )
}

export default CombineRules;
