import React, { useState } from 'react';
import axios from 'axios';

function EvaluateRule() {
  const [ruleName, setRuleName] = useState('');
  const [dataJSON, setDataJSON] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:5000/evaluate_rule', {
      rule_string: ruleName,
      attributes: JSON.parse(dataJSON)
    }).then(response => {
      alert('Evaluation Result: ' + response.data.result);
    }).catch(error => {
      console.error('Error evaluating rule', error);
    });
  };

  return (
    <div>
      <h2>Evaluate Rule</h2>
      <form onSubmit={handleSubmit}>
        <label>Rule Name:</label>
        <input type="text" value={ruleName} onChange={(e) => setRuleName(e.target.value)} required />
        <br />
        <label>Data JSON:</label>
        <textarea
          value={dataJSON}
          onChange={(e) => setDataJSON(e.target.value)}
          required
        />
        <br />
        <button type="submit">Evaluate Rule</button>
      </form>
    </div>
  );
}

export default EvaluateRule;
