import React, { useState } from 'react';
import axios from 'axios';

function CreateRule() {
  const [ruleName, setRuleName] = useState('');
  const [rule, setRule] = useState('');
  const [message, setMessage] = useState('');
  const [wrongMessage, setWrongMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://127.0.0.1:5000/create_rule', {
        rule_name: ruleName,
        rule: rule
      });
      setMessage(response.data.message); // Adjust according to your response structure
      setRuleName('');
      setRule('');
      setWrongMessage('');
    } catch (error) {
      setWrongMessage(error.response.data.message || 'Error creating rule!'); // Adjust according to your error response structure
      setMessage('');
    }
  };

  return (
    <div className="bg-gray-200 p-6 rounded-lg shadow-lg ring-1 ring-gray-300 w-full max-w-lg mx-auto">
      <h2 className="text-2xl font-semibold text-gray-800 mb-4">Create Rule</h2>
      <form onSubmit={handleSubmit} className="space-y-6">
        <div className="space-y-2">
          <label className="block text-gray-700 font-medium">Rule Name:
            <input
              type="text"
              value={ruleName}
              onChange={(e) => setRuleName(e.target.value)}
              required
              className="mt-1 w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400 transition duration-300 ease-in-out"
            />
          </label>
        </div>
        <div className="space-y-2">
          <label className="block text-gray-700 font-medium">Rule:
            <input
              type="text"
              value={rule}
              onChange={(e) => setRule(e.target.value)}
              required
              className="mt-1 w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-400 transition duration-300 ease-in-out"
            />
          </label>
        </div>
        <button type="submit" className="w-full bg-blue-500 text-white font-semibold py-2 rounded-lg hover:bg-blue-600 transition duration-300 ease-in-out transform hover:scale-105">Create Rule</button>
      </form>
      {message && <p className="mt-4 p-4 bg-green-100 border border-green-400 font-semibold text-green-800 rounded-md">{message}</p>}
      {wrongMessage && <p className="mt-4 p-4 bg-red-100 border border-red-400 text-red-800 rounded-md">{wrongMessage}</p>}
    </div>
  );
}

export default CreateRule;
