import React,  { useState }  from 'react';
import CreateRule from './Components/CreateRule';
import CombineRules from './Components/CombineRules';
import EvaluateRule from './Components/EvaluateRule';
import './App.css';

const App = () => {

  const [ActiveComponent, setActiveComponent] = useState('cre');

  return (
    <>
     <div className='bg-gray-100 min-h-screen flex flex-col items-center justify-center p-6'>
        <h1 className='text-3xl font-bold text-gray-800 mb-6'> Rule Engine AST (Abstarct Syntax Tree)</h1>
        <div className='flex space-x-4 mb-8'>
          <button className='px-6 py-2 bg-blue-500 text-white font-semibold rounded-lg hover:bg-blue-600 transform transition duration-300 hover:scale-105 active:scale-95 focus:outline-none' onClick={() => setActiveComponent('cre')}> Create Rule </button>
          <button className='px-6 py-2 bg-green-500 text-white font-semibold rounded-lg hover:bg-green-600 transition duration-300 ease-in-out transform hover:scale-105 active:scale-95 focus:outline-none' onClick={() => setActiveComponent('comb')}> Combine Rule </button>
          <button className="px-6 py-2 bg-red-500 text-white font-semibold rounded-lg hover:bg-red-600 transition duration-300 ease-in-out transform hover:scale-105 active:scale-95 focus:outline-none" onClick={() => setActiveComponent('eval')}> Evaluate Rule </button>
        </div>
        <div className='w-full max-w-4xl'>
          {
            ActiveComponent === 'cre' && (
              <div className="transition-opacity duration-500 ease-in opacity-100">
                <CreateRule />
              </div>  
            )}
          {
            ActiveComponent === 'comb' && (
              <div>
                <CombineRules />
              </div>
            )}
          {
            ActiveComponent === 'eval' && <EvaluateRule />
          }
        </div>
     </div>
    </>
  )
}

export default App;
