import React, { useState } from 'react';
import axios from 'axios';

function EvaluateRule() {
  const [ruleName, setRuleName] = useState('');
  const [dataJSON, setDataJSON] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:5000/evaluate_rule', {
      rule_string: ruleName,
      attributes: JSON.parse(dataJSON)
    }).then(response => {
      alert('Evaluation Success: ' + response.data.result);
    }).catch(error => {
      console.error('Error evaluating rule', error);
    });
  };

  return (
    <div className="bg-gray-100 p-6 rounded-lg shadow-lg w-full max-w-lg mx-auto">
        <h2 className="text-2xl font-semibold text-gray-800 mb-4">Evaluate Rule</h2>
        <form onSubmit={handleSubmit} className="space-y-6">
            <div className="space-y-2">
                <label className="block text-gray-700 font-medium">Rule Name:
                    <input 
                        type="text" 
                        value={ruleName} 
                        onChange={(e) => setRuleName(e.target.value)} 
                        required  
                        className="mt-1 w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-400 transition duration-300 ease-in-out"
                    />
                </label>
            </div>
            <div className="space-y-2">
                <label className="block text-gray-700 font-medium">Data JSON:
                    <textarea 
                        value={dataJSON} 
                        onChange={(e) => setDataJSON(e.target.value)} 
                        required 
                        rows={5} 
                        className="mt-1 w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-green-400 transition duration-300 ease-in-out"
                    />
                </label>
            </div>
            <button 
                type="submit" 
                className="w-full bg-blue-500 text-white font-semibold py-2 rounded-lg hover:bg-blue-600 transition duration-300 ease-in-out transform hover:scale-105"
            >
            Evaluate Rule
            </button>
        </form>
    </div>
  );
}

export default EvaluateRule;
