import React, {useState} from 'react';
import axios from 'axios';

const CombineRules = () => {

    const [rules, setRules] = useState([{ ruleName: '', operator: 'AND' }]);
    
    const handleAddRule = () => {
        setRules([ ...rules, { ruleName: '', operator: 'AND' }]);
    }

    const handleRuleChange = (index, field )

  return (
    <>
        <h2>Combine Rules</h2>
        <form onSubmit={() => handleSubmit}>
            {
                rules.map((rule, index) => (
                    <div key={index}>
                        <label>
                            Rule { index + 1 }:
                            <input
                                type='text' 
                                value={rule.ruleName}
                                onChange={(e) => handleRuleChange(index, 'ruleName', e.target.value)}
                                required
                            />
                            {
                                index < rules.length - 1 && (
                                    <div>
                                        <label>
                                            Operator:
                                            <select
                                                value={rule.operator}
                                                onChange={(e) => handleRuleChange(index, 'operator', e.target.value)}
                                            >
                                                <option value="AND">AND</option>
                                                <option value="OR">OR</option>
                                            </select>
                                        </label>
                                    </div>
                                )
                            }
                        </label>
                    </div>
                ))}
                <button type="button" onClick={() => handleAddRule}>Add Another Rule</button>
                <button type="submit">Combine Rules</button>
        </form>
    </>
  )
}

export default CombineRules;
