// App.js

import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [rule, setRule] = useState('');
  const [rule1Id, setRule1Id] = useState('');
  const [rule2Id, setRule2Id] = useState('');
  const [operator, setOperator] = useState('AND');
  const [combinedRule, setCombinedRule] = useState('');
  const [ruleIdToEvaluate, setRuleIdToEvaluate] = useState('');
  const [evaluatedRule, setEvaluatedRule] = useState('');

  // Handle rule creation
  const handleCreateRule = () => {
    axios.post('http://localhost:5000/create_rule', { rule })
      .then(response => {
        alert(response.data.message);
        setRule('');
      })
      .catch(error => {
        console.error("There was an error creating the rule!", error);
      });
  };

  // Handle rule combination
  const handleCombineRules = () => {
    axios.post('http://localhost:5000/combine_rules', {
      rule1_id: rule1Id,
      rule2_id: rule2Id,
      operator
    })
    .then(response => {
      setCombinedRule(response.data.combined_rule);
    })
    .catch(error => {
      console.error("There was an error combining the rules!", error);
    });
  };

  // Handle rule evaluation
  const handleEvaluateRule = () => {
    axios.post('http://localhost:5000/evaluate_rule', { rule_id: ruleIdToEvaluate })
      .then(response => {
        setEvaluatedRule(response.data.rule_text);
      })
      .catch(error => {
        console.error("There was an error evaluating the rule!", error);
      });
  };

  return (
    <div className="App" style={{ padding: '20px' }}>
      <h1>Rule Engine with AST</h1>

      {/* Create Rule Section */}
      <div style={{ marginBottom: '20px' }}>
        <h3>Create Rule</h3>
        <input 
          type="text" 
          value={rule} 
          onChange={(e) => setRule(e.target.value)} 
          placeholder="Enter rule here" 
        />
        <button onClick={handleCreateRule} style={{ marginLeft: '10px' }}>
          Create Rule
        </button>
      </div>

      {/* Combine Rules Section */}
      <div style={{ marginBottom: '20px' }}>
        <h3>Combine Rules</h3>
        <input 
          type="text" 
          value={rule1Id} 
          onChange={(e) => setRule1Id(e.target.value)} 
          placeholder="Rule 1 ID" 
        />
        <input 
          type="text" 
          value={rule2Id} 
          onChange={(e) => setRule2Id(e.target.value)} 
          placeholder="Rule 2 ID" 
          style={{ marginLeft: '10px' }}
        />
        <select 
          value={operator} 
          onChange={(e) => setOperator(e.target.value)} 
          style={{ marginLeft: '10px' }}>
          <option value="AND">AND</option>
          <option value="OR">OR</option>
        </select>
        <button onClick={handleCombineRules} style={{ marginLeft: '10px' }}>
          Combine Rules
        </button>
        {combinedRule && <p>Combined Rule: {combinedRule}</p>}
      </div>

      {/* Evaluate Rule Section */}
      <div style={{ marginBottom: '20px' }}>
        <h3>Evaluate Rule</h3>
        <input 
          type="text" 
          value={ruleIdToEvaluate} 
          onChange={(e) => setRuleIdToEvaluate(e.target.value)} 
          placeholder="Enter rule ID to evaluate" 
        />
        <button onClick={handleEvaluateRule} style={{ marginLeft: '10px' }}>
          Evaluate Rule
        </button>
        {evaluatedRule && <p>Evaluated Rule: {evaluatedRule}</p>}
      </div>
    </div>
  );
}

export default App;
