import React,  { useState }  from 'react';
import CreateRule from './Components/CreateRule';
import CombineRules from './Components/CombineRules';
import EvaluateRule from './Components/EvaluateRule';
import './App.css';

const App = () => {

  const [ActiveComponent, setActiveComponent] = useState('cre');

  return (
    <>
     <div className='bg-gray-100 min-h-screen flex flex-col items-center justify-center p-6'>
        <h1 className='text-3xl font-bold text-gray-800 mb-6'> Rule Engine AST Application </h1>
        <div className='flex space-x-4 mb-8'>
          <button className='px-6 py-2 bg-blue-500 text-white font-semibold rounded-lg hover:bg-blue' onClick={() => setActiveComponent('cre')}> Create Rule </button>
          <button onClick={() => setActiveComponent('comb')}> Combine Rule </button>
          <button onClick={() => setActiveComponent('eval')}> Evaluate Rule </button>
        </div>
        {
          ActiveComponent === 'cre' && <CreateRule />
        }
        {
          ActiveComponent === 'comb' && <CombineRules />
        }
        {
          ActiveComponent === 'eval' && <EvaluateRule />
        }
     </div>
    </>
  )
}

export default App;
